import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [items, setItem] = useState([]);

  useEffect(async () => {
      const result = axios('http://localhost:3000');

      setItem(result.items)
  });

  return (
    <div className="App">
      <p>bonjour</p>
      {items.map(i => <p>{i.title}</p>)}
    </div>
  );
}

export default App;
