import React, { useEffect } from 'react';
import { Bar } from 'react-chartjs-2';

const ChartItem = (data) => {
    const prices = data.data.map(item => item.price);
    const title = data.data.map(item => item.title);
    const opacity = 0.5;
    //const color = `rgba(120, 40, 31, ${opacity})`;

    const chooseColor = (color) => {
        const lastElement = prices[prices.length - 1];
        for(let i = 0; i < prices.length; i++) {
            opacity = prices.length * 0,1;
            return console.log(opacity);
        }
    }

    const dataItem = {
        labels: title,
        datasets: [{
            backgroundColor: 'rgba(120, 40, 31, 1)',
            data: prices
        }]
    }

    return (
        <div className="ChartItem">
            <h1>Chart</h1>
            <Bar data={dataItem}/>
        </div>
    )
}

export default ChartItem;
